() => {
  // ---- Sua Chave Gemini Aqui
  const apiKey = "AIzaSyC8MD4ZTRedYmVIvwfAAVCX-OthA6QQ37c";

  // ---- Gemini API
  async function generateContent() {
    const url =
      "https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=" +
      apiKey;

    const data = {
      contents: [
        {
          parts: [{ text: "Explique em pt-br como funciona uma IA" }],
        },
      ],
    };

    try {
      await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });
    } catch (error) {
      console.error("Erro na requisição:", error);
    }
  }

  // ---- Styles
  const stlCenter = {
    alignItems: "center",
    justifyContent: "center",
  };

  const stlBtn = {
    ...stlCenter,
    backgroundColor: "darkcyan",
    width: 100,
    height: 24,
  };

  const stlTxt = {
    color: "white",
  };

  const stlInput = {
    color: "white",
  };

  const stlContainer = {
    flex: 1,
    width: "100%",
    height: "100%",
    ...stlCenter,
    backgroundColor: "#333",
  };

  return (
    <RN.View style={stlContainer}>
      <RN.TextInput style={stlInput} />
      <RN.Pressable style={stlBtn} onPress={generateContent}>
        <RN.Text style={stlTxt}>GEMINI</RN.Text>
      </RN.Pressable>
    </RN.View>
  );
}
